import React, { Component, useState } from 'react';
import { Helmet } from "react-helmet";

import "./assets/css/styles.css"
import 'bootstrap/dist/js/bootstrap.bundle'

import date from 'date-and-time';

import Clock from 'react-live-clock';

import { render } from "react-dom";
import ReactDOM from 'react-dom'

import Form from 'react-bootstrap/Form'


import { Button, Modal, Container, Row, Col } from 'react-bootstrap';
import Alert from 'react-bootstrap/Alert';


import Header from './components/Header'
import Body from './components/Body'
import Timer from './components/Timer'

import $ from 'jquery';


class App extends React.Component {
  //For determining location based on IP address
  componentDidMount() {
    $.get(
      "https://ipinfo.io/json?token=5ec5b65121ca42",
      //"https://api.allorigins.win/raw?url=https://ipinfo.io/json",
      function (response) {
        //$("#ip").html("IP: " + response.ip);
        $("address").html(response.city);
        //$("#address").html(response.city + ", " + response.region);
        //$("#details").html(JSON.stringify(response, null, 4));
      },
      "json")

    var url = "https://ipinfo.io/json?token=5ec5b65121ca42";
    fetch(url)
      .then(response => {
        return response.json();
      })
      .then(d => {
        this.setState({ clouds: d });
        console.log("state", this.state.clouds)
      })
      .catch(error => console.log(error))
  }

  render() {
    return (
      <>
        {
          this.state.clouds.map(((cloud, index) =>
            <th key={`${cloud.cloud}${index}`}>
              <div>
                <div>
                  {cloud.cloud}
                  <div>
                    {
                      cloud.data_centers.map(d => (
                        <div>
                          {d.title}
                        </div>
                      ))
                    }
                  </div>
                </div>
              </div>
            </th>
          ))
        }



        <Header />

        {/* <hr />
        <div id="ip"></div>
        <div id="address"></div>
        <hr />Full response: <pre id="details"></pre> */}




        <Body city='<div id="address"></div>' />
        <Attendance />
      </>
    );
  }
}

export default App;





function Attendance() {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Attendance
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
